package day20_arrays;

public class C09_MethodOlusturmak {
	
	public static void birSayininKaresiniAlma(int sayi) {
		int sayininKaresi= sayi*sayi;
		System.out.println("Girilen Sayinin Karesi: "+ sayininKaresi);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		birSayininKaresiniAlma(4);

		birSayininKaresiniAlma(14);
		
		birSayininKaresiniAlma(24);
	}

}
